package mypack;
import java.util.*;
public class IncrementAndDecrementOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		System.out.println("After incrementation: "+ (++n));
		System.out.println("After decrementation: "+ (--n));
		sc.close();
		

	}

}
